import { action } from '@storybook/addon-actions';
import { storiesOf } from '@storybook/react';
import _ from 'lodash';
import * as React from 'react';

import { Agreement, FixedBottomCTA } from 'src/react';

import Readme from '../README.md';

storiesOf('Agreement', module)
  .addParameters({ readme: { sidebar: Readme } })
  .add('📓example - 02 + 03', () => {
    function AgreementRow02And03Example() {
      const [[mustChecked, optionalChecked], setChecked] = React.useState([false, false]);

      function onChangeAllChecked(event: React.ChangeEvent<HTMLInputElement>) {
        const { checked } = event.target;
        setChecked([checked, checked]);
        action('onChange - Agreement02 - 토스 필수 항목 모두 동의')(event);
      }

      function onClickStartButton() {
        if (mustChecked) {
          alert(`성공!`);
        } else {
          alert('필수 약관에 동의해주세요.');
        }
      }

      return (
        <>
          <Agreement.Section>
            <Agreement.List>
              <Agreement.Row02
                checked={mustChecked && optionalChecked}
                onChange={onChangeAllChecked}
              >
                토스 필수 항목 모두 동의
              </Agreement.Row02>
              {_.times(4).map(index => (
                <Agreement.Row03
                  onClick={action(`onClick - AgreementRow03 - 필수 항목 ${index + 1}`)}
                  key={`agreement-${index}`}
                >
                  필수 항목 {index + 1}
                </Agreement.Row03>
              ))}
            </Agreement.List>
          </Agreement.Section>
          <FixedBottomCTA onClick={onClickStartButton}>시작</FixedBottomCTA>
        </>
      );
    }

    return <AgreementRow02And03Example />;
  });
