import classNames from "classnames";
import * as React from "react";
import { ComponentProps, HTMLAttributes } from "react";
import { Icon } from "../Icon";

interface BaseProps {
  name?: string | undefined;
  src?: string | undefined;
}

interface IconProps extends ComponentProps<typeof Icon>, BaseProps {
  name: string;
}

interface ImageProps extends HTMLAttributes<HTMLImageElement>, BaseProps {
  src: string;
}

type Props = IconProps | ImageProps;

export default function BannerIcon({ className, ...props }: Props) {
  if ("name" in props) {
    return (
      <Icon
        className={classNames(className, "banner__icon")}
        size={40}
        {...(props as IconProps)}
      />
    );
  } else if ("src" in props) {
    return <img className={classNames(className, "banner__icon")} {...props} />;
  }

  return null;
}
