import { action } from '@storybook/addon-actions';
import { storiesOf } from '@storybook/react';
import * as React from 'react';

import { BoardRow } from 'src/react';

import Readme from '../README.md';

storiesOf('BoardRow', module)
  .addParameters({ readme: { sidebar: Readme } })
  // Props로 열림 / 닫힘을 제어하기 위해 사용됩니다.
  .add('isOpened', () => {
    function BoardRowIsOpenExample() {
      const [isOpened, setIsOpened] = React.useState(false);

      function onOpen() {
        setIsOpened(true);
        action('onOpen - BoardRow')(...arguments);
      }

      function onClose() {
        setIsOpened(false);
        action('onClose - BoardRow')(...arguments);
      }

      return (
        <BoardRow title="매도 환전이란?" isOpened={isOpened} onOpen={onOpen} onClose={onClose}>
          <p>주식 거래가 실시간이 아니기 때문에, 가격이 변할 것에 대비하는 금액을 말합니다.</p>
          <p>이 금액은 수수료가 아니며, 주식 거래에 쓰이고 남으면 계좌로 환급됩니다.</p>
        </BoardRow>
      );
    }

    return <BoardRowIsOpenExample />;
  });
