import { action } from "@storybook/addon-actions";
import { boolean, select } from "@storybook/addon-knobs";
import { storiesOf } from "@storybook/react";
import * as React from "react";
import { BottomCTA, BottomSheet, Container } from "src/react";
import { ButtonType } from "../../Button";
import Readme from "../README.md";
import Paragraphs from "./Paragraphs";

storiesOf("BottomCTA", module)
  .addParameters({ readme: { sidebar: Readme } })
  .add("TypeA", () => (
    <BottomCTA
      onClick={action("버튼 클릭")}
      disabled={boolean("disabled", false)}
      loading={boolean("loading", false)}
      type={select<ButtonType>(
        "buttonType",
        ["primary", "light", "dark"],
        "primary"
      )}
      background={select("background", ["default", "none"], "default")}
    >
      신청하기
    </BottomCTA>
  ))
  .add("TypeA : as Fixed element ", () => (
    <Container>
      <Paragraphs />
      <BottomCTA
        onClick={action("버튼 클릭")}
        disabled={boolean("disabled", false)}
        loading={boolean("loading", false)}
        background={select("background", ["default", "none"], "default")}
        fixed={true}
      >
        신청하기
      </BottomCTA>
    </Container>
  ))
  .add("TypeA: 매우 긴 텍스트", () => {
    return (
      <Container>
        <Paragraphs />
        <BottomCTA
          onClick={action("버튼 클릭")}
          disabled={boolean("disabled", false)}
          loading={boolean("loading", false)}
          background={select("background", ["default", "none"], "default")}
          fixed={true}
        >
          버튼 레이블이 길면 두줄로 나옵니다
        </BottomCTA>
      </Container>
    );
  })
  .add("TypeA : with BottomSheet ", () => (
    <BottomSheet
      open={true}
      header={
        <BottomSheet.Header>상담을 하시려면 선택해주세요.</BottomSheet.Header>
      }
    >
      <Paragraphs />
      <BottomSheet.CTA>신청하기</BottomSheet.CTA>
    </BottomSheet>
  ));
