import { select } from "@storybook/addon-knobs";
import { storiesOf } from "@storybook/react";
import * as React from "react";
import { BottomCTA, Container } from "src/react";
import BottomSheet from "../../BottomSheet";
import { Button } from "../../Button";
import Readme from "../README.md";
import Paragraphs from "./Paragraphs";

storiesOf("BottomCTA", module)
  .addParameters({ readme: { sidebar: Readme } })
  .add("📓example - TypeB", () => (
    <BottomCTA.TypeB
      leftButton={<Button type="danger">Secondary</Button>}
      rightButton={<Button type="primary">Primary</Button>}
      background={select("background", ["default", "none"], "default")}
    />
  ))
  .add("📓example - TypeB : as Fixed element ", () => (
    <Container>
      <Paragraphs />
      <BottomCTA.TypeB
        fixed={true}
        leftButton={<Button type="danger">Secondary</Button>}
        rightButton={<Button type="primary">Primary</Button>}
        background={select("background", ["default", "none"], "default")}
      />
    </Container>
  ))
  .add("📓example - TypeB : with BottomSheet ", () => (
    <BottomSheet
      open={true}
      header={<BottomSheet.Header>계속하려면 확인하세요</BottomSheet.Header>}
    >
      <Paragraphs />
      <BottomSheet.CTA.TypeB
        leftButton={<Button type="danger">Secondary</Button>}
        rightButton={<Button type="primary">Primary</Button>}
      />
    </BottomSheet>
  ));
