import classNames from 'classnames';
import * as React from 'react';

import { CardBackground } from './CardBackground';

interface Props {
  className?: string;
}

export class CardBackgrounds extends React.PureComponent<Props> {
  public static Item = CardBackground;

  public render() {
    const { className, children } = this.props;

    return <div className={classNames(className, 'card-backgrounds')}>{children}</div>;
  }
}
