import { storiesOf } from '@storybook/react';
import * as React from 'react';

import Carousel from 'src/react/Carousel';

import Readme from '../README.md';
import SimpleSlide from './SimpleSlide';

storiesOf('Carousel', module)
  .addParameters({ readme: { sidebar: Readme } })
  .add('centeredSlides', () => {
    const extraProps = { spaceBetween: 16, slidesPerView: 1.2 };

    return (
      <>
        {[false, true].map(centeredSlides => (
          <>
            <Carousel {...extraProps} centeredSlides={centeredSlides}>
              {[1, 2, 3, 4, 5].map(index => (
                <Carousel.Slide key={index}>
                  <SimpleSlide>centeredSlides = {centeredSlides.toString()}</SimpleSlide>
                </Carousel.Slide>
              ))}
            </Carousel>
            <br />
          </>
        ))}
      </>
    );
  });
