import { storiesOf } from '@storybook/react';
import * as React from 'react';

import Carousel from 'src/react/Carousel';

import Readme from '../README.md';
import SimpleSlide from './SimpleSlide';

storiesOf('Carousel', module)
  .addParameters({ readme: { sidebar: Readme } })
  .add('spaceBetween', () => {
    const spaceBetweens = [8, 16, 24];
    const extraProps = { slidesPerView: 1.2, centeredSlides: true };

    return (
      <>
        {spaceBetweens.map(spaceBetween => (
          <>
            <Carousel {...extraProps} spaceBetween={spaceBetween}>
              {[1, 2, 3, 4, 5].map(index => (
                <Carousel.Slide key={index}>
                  <SimpleSlide>spaceBetween = {spaceBetween}</SimpleSlide>
                </Carousel.Slide>
              ))}
            </Carousel>
            <br />
          </>
        ))}
      </>
    );
  });
