import cx from "classnames";
import * as React from "react";

export interface Props
  extends React.DetailedHTMLProps<
    React.HTMLAttributes<HTMLDivElement>,
    HTMLDivElement
  > {
  innerCenter?: boolean;
  transition?: boolean;
}

function Dimmer({
  innerCenter = false,
  transition = false,
  className,
  ...divProps
}: Props) {
  return (
    <div
      {...divProps}
      className={cx(
        "dimmer",
        {
          "dimmer--inner-center": innerCenter,
          "dimmer--transition": transition,
        },
        className
      )}
    />
  );
}

export default Dimmer;
