import classnames from 'classnames';
import * as React from 'react';

interface Props {
  children?: never;

  /**
   * 컴포넌트의 root element(`div`)에 추가되는 className입니다.
   */
  className?: string;

  /**
   * 제목을 지정합니다.
   */
  title?: string;

  /**
   * 우측에 나타날 보조 문구를 지정합니다.
   */
  subtext?: string;
}

export default class Form extends React.Component<Props> {
  public render() {
    const { className, title, subtext } = this.props;
    return (
      <div className={classnames('list-header', 'form-legend', className)}>
        <legend className="typography-st8">{title}</legend>
        <div className="typography-t7">{subtext}</div>
      </div>
    );
  }
}
