import * as React from "react";
import { forwardRef, HTMLAttributes } from "react";
import Txt from "../Text";

interface Props extends HTMLAttributes<HTMLDivElement> {
  image: {
    src: string;
    alt?: string;
  };
}

const GridListContent = forwardRef<HTMLDivElement, Props>(
  ({ className, children, image, ...rest }, ref) => {
    return (
      <div className="grid-list__item__wrapper" ref={ref} {...rest}>
        <div className="grid-list__item__content">
          <img
            className="grid-list__item__image"
            src={image.src}
            alt={image.alt}
            aria-hidden={image.alt === undefined ? true : undefined}
          />
          <Txt typography="st11" fontWeight="medium">
            {children}
          </Txt>
        </div>
      </div>
    );
  }
);

export default GridListContent;
