import classnames from 'classnames';
import * as React from 'react';

import { Icon } from '../Icon';

export interface Props {
  className?: string;
  iconClassName?: string;

  /**
   * 아이콘의 이름입니다.
   */
  name: string;

  /**
   * 아이콘의 색상을 지정합니다.
   */
  color?: string;

  /**
   * 아이콘의 크기를 지정합니다.
   */
  size?: number;

  onClick: (e: React.MouseEvent<HTMLButtonElement>) => void;
}

// Deprecated.
export default class IconButton extends React.Component<Props> {
  public render() {
    const { onClick, className, iconClassName, name, color, size } = this.props;
    return (
      <button className={classnames('icon-button', className)} onClick={onClick}>
        <Icon className={iconClassName} name={name} color={color} size={size} />
      </button>
    );
  }
}
