import { button } from '@storybook/addon-knobs';
import { storiesOf } from '@storybook/react';
import * as React from 'react';

import { List, ListRow } from 'src/react';

function FlickerExample() {
  const ref: React.RefObject<ListRow> = React.useRef(null);

  const flicker = () => {
    ref.current!.flicker();
  };

  return (
    <>
      <List rowSeparator="full">
        <ListRow ref={ref as any} contents="Flickering" />
        <ListRow contents="............" />
      </List>
      {button('Flicker', flicker)}
    </>
  );
}

storiesOf('ListRow', module).add('ðŸŽ¬flicker', () => {
  return <FlickerExample />;
});
