import { action } from "@storybook/addon-actions";
import { boolean } from "@storybook/addon-knobs";
import { storiesOf } from "@storybook/react";
import * as React from "react";
import { useState } from "react";
import Radio from "../Radio";

storiesOf("Radio", module)
  .addDecorator((storyFn) => {
    return <div style={{ padding: "24px" }}>{storyFn()}</div>;
  })
  .add("Uncontrolled", () => {
    const disabled = boolean("disabled", false);

    return (
      <Radio onChange={action("onChange")} disabled={disabled}>
        <Radio.Option value="DPR LIVE">DPR LIVE </Radio.Option>
        <Radio.Option value="CRUSH">CRUSH</Radio.Option>
      </Radio>
    );
  })
  .add("Controlled", () => {
    const [value, setValue] = useState<"DEAN" | "COLDE" | "DPR LIVE">("DEAN");

    return (
      <Radio
        value={value}
        onChange={({ target: { value } }) => {
          setValue(value as "DEAN" | "COLDE");
        }}
      >
        <Radio.Option value="DEAN">DEAN</Radio.Option>
        <Radio.Option value="COLDE">COLDE</Radio.Option>
        <Radio.Option value="DPR LIVE">DPR LIVE</Radio.Option>
      </Radio>
    );
  });
