import { storiesOf } from '@storybook/react';
import * as React from 'react';

import { Text, Typography } from 'src/react';

import Readme from '../README.md';

storiesOf('Text', module)
  .addParameters({ readme: { sidebar: Readme } })
  .add('typography', () => (
    <>
      {Object.entries(Typography).map(([key, value], index) => (
        <Text typography={value} display="block" key={`text-${index}`}>
          typography {key}
        </Text>
      ))}
    </>
  ));
