import { action } from '@storybook/addon-actions';
import { storiesOf } from '@storybook/react';
import * as React from 'react';

import { TextArea } from 'src/react';

import Readme from '../README.md';

storiesOf('TextArea', module)
  .addParameters({ readme: { sidebar: Readme } })
  .add('value & onChange', () => {
    function TextAreaControlledExample() {
      const [value, setValue] = React.useState('');

      function onChangeTextArea(event: React.ChangeEvent<HTMLTextAreaElement>) {
        setValue(event.target.value);
        action('onChange - TextArea')(...arguments);
      }

      return <TextArea value={value} onChange={onChangeTextArea} />;
    }

    return <TextAreaControlledExample />;
  });
