declare module 'octonode' {
  interface Issue {
    createCommentAsync(options: { body: string }): Promise<any>;
    commentsAsync(): Promise<[Array<{ user: { login: string } }>]>;
  }

  interface PullRequest {
    infoAsync(): Promise<
      [{ user: { login: string }; requested_reviewers?: Array<{ login: string }> }]
    >;
    createReviewRequestsAsync(userIds: string[]): Promise<any>;
  }

  interface Client {
    issue(repoName: string, issueNo: number): Issue;
    pr(repoName: string, issueNo: number): PullRequest;
  }

  interface GitHub {
    client(accessKey: string): Client;
  }

  const github: GitHub;

  export = github;
}
