type Category = 'component' | 'tool' | 'deprecated' | 'lab';

const directoryNameByCategory: Record<Category, string> = {
  component: '',
  tool: '10. 도구/',
  deprecated: '998. 폐기예정/',
  lab: '999. 실험실/',
};

export const makeDisplayName = (category: Category) => (componentName: string, koreanName: string) =>
  `${directoryNameByCategory[category]}${componentName} ・ ${koreanName}`;

export const makeComponentDisplayName = makeDisplayName('component');
export const makeDeprecatedComponentDisplayName = makeDisplayName('deprecated');
