import classnames from "classnames";
import * as React from "react";
import { ComponentProps, HTMLProps, ReactNode } from "react";
import Checkbox from "../Checkbox";

interface Props
  extends Omit<HTMLProps<HTMLLIElement>, "label" | "checked" | "onChange"> {
  checked?: boolean;
  onChange?: ComponentProps<typeof Checkbox>["onChange"];
  right?: ReactNode;
}

export default function AgreementRow01({
  checked,
  onChange,
  right,
  className,
  children,
  ...props
}: Props) {
  return (
    <li className={classnames("agreement-row-01", className)} {...props}>
      <Checkbox
        className={classnames("agreement-row-01__checkbox", "typography-st10")}
        checked={checked}
        onChange={onChange}
        type="circle-big-primary"
      >
        {children}
      </Checkbox>
      {right}
    </li>
  );
}
