import { storiesOf } from '@storybook/react';
import * as React from 'react';
import { select } from '@storybook/addon-knobs';
import styled from 'styled-components';

import { Badge } from 'src/react';

import Readme from '../README.md';

type BadgeProps = React.ComponentProps<typeof Badge>;

storiesOf('Badge', module)
  .addParameters({ readme: { sidebar: Readme } })
  .add('Overview', () => {
    const styles: Array<BadgeProps['style']> = ['fill', 'weak'];
    const types: Array<BadgeProps['type']> = ['blue', 'teal', 'green', 'red', 'yellow', 'elephant'];
    const size = select('Size', ['large', 'medium', 'small'], 'small');

    return (
      <Wrapper>
        {styles.map(style =>
          types.map(type => (
            <Badge key={`${style}-${type}`} type={type} style={style} size={size}>
              {style} - {type}
            </Badge>
          ))
        )}
      </Wrapper>
    );
  });

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 12px;

  & > .badge {
    margin: 0;
    margin-bottom: 12px;
  }
`;
