import classnames from 'classnames';
import * as React from 'react';

import BottomInfoItem from './BottomInfoItem';

type BottomInfoItemElement = React.ReactElement<BottomInfoItem>;

type InfoType = 'list' | 'text';

export interface Props {
  type?: InfoType;
  className?: string;
  children: BottomInfoItemElement | BottomInfoItemElement[];
}

export default class BottomInfo extends React.PureComponent<Props> {
  public static Item = BottomInfoItem;

  public render() {
    const isText = this.props.type === 'text';

    return React.createElement(
      isText ? 'div' : 'ul',
      {
        className: classnames(`bottom-info`, { 'bottom-info--text': isText }, this.props.className),
      },
      this.props.children
    );
  }
}
