import { boolean, select } from "@storybook/addon-knobs";
import { storiesOf } from "@storybook/react";
import * as React from "react";
import TextButton from "../TextButton";
import Bubble from "./Bubble";

storiesOf("Bubble", module)
  .addDecorator((storyFn) => <div style={{ padding: "24px" }}>{storyFn()}</div>)
  .add("Basics", () => {
    return (
      <Bubble
        background={select("Background", ["blue", "grey"], "blue")}
        withTail={boolean("Tail", true)}
      >
        길게 썼을 때 말풍선의 예시입니다 동해물과 백두산이 마르고 닳도록
        하느님이 보우하사
      </Bubble>
    );
  })
  .add("With TextButton", () => {
    return (
      <Bubble background="grey" withTail={boolean("Tail", true)}>
        <TextButton type="primary" withArrow={true}>
          텍스트 버튼
        </TextButton>
      </Bubble>
    );
  });
