import { storiesOf } from '@storybook/react';
import * as React from 'react';

import Carousel from 'src/react/Carousel';

import Readme from '../README.md';
import SimpleSlide from './SimpleSlide';

storiesOf('Carousel', module)
  .addParameters({ readme: { sidebar: Readme } })
  .add('paginationPosition', () => {
    const paginationPositions: [
      'top-left',
      'top',
      'top-right',
      'bottom-left',
      'bottom',
      'bottom-right'
    ] = ['top-left', 'top', 'top-right', 'bottom-left', 'bottom', 'bottom-right'];
    const extraProps = { spaceBetween: 16 };

    return (
      <>
        {paginationPositions.map(paginationPosition => (
          <>
            <Carousel {...extraProps} paginationPosition={paginationPosition}>
              {[1, 2, 3, 4, 5].map(index => (
                <Carousel.Slide key={index}>
                  <SimpleSlide>paginationPosition = {paginationPosition}</SimpleSlide>
                </Carousel.Slide>
              ))}
            </Carousel>
            <br />
          </>
        ))}
      </>
    );
  });
