import classnames from 'classnames';
import * as React from 'react';

export interface Props {
  className?: string;
  divAttributes?: React.HTMLProps<HTMLDivElement>;
  contentDivAttributes?: React.HTMLProps<HTMLDivElement>;
}

export const ContainerContext = React.createContext(false);

export default class Container extends React.PureComponent<Props> {
  public render() {
    const { className, children, divAttributes, contentDivAttributes = {} } = this.props;

    return (
      <ContainerContext.Provider value={true}>
        <div {...divAttributes} className={classnames('container', className)}>
          <div
            {...contentDivAttributes}
            className={classnames('container__content', contentDivAttributes.className)}
          >
            {children}
          </div>
        </div>
      </ContainerContext.Provider>
    );
  }
}
