import cx from "classnames";
import * as React from "react";
import { ReactNode } from "react";
import GridListItem01 from "./GridListItem01";

export interface Props {
  /**
   * 컴포넌트의 root element(`div`)에 추가되는 className입니다.
   */
  className?: string;

  /**
   * 표시될 열의 개수를 정합니다.
   */
  column?: 1 | 2 | 3;

  children?: ReactNode;

  style?: React.CSSProperties;
}

function GridList01({ className, column = 3, children, style }: Props) {
  return (
    <div
      className={cx(
        "grid-list",
        `grid-list--type-01`,
        `grid-list--column-${column}`,
        `grid-list--column-gap-8`,
        `grid-list--row-gap-8`,
        className
      )}
      style={style}
    >
      <ul className="grid-list__list">{children}</ul>
    </div>
  );
}

GridList01.Item = GridListItem01;

export default GridList01;
