import * as React from "react";
import { useMemo } from "react";
import Backspace from "./Backspace";

export enum SPECIAL_KEY {
  blank = "blank",
  backspace = "backspace",
}

interface Props {
  char: string;
  onKeyClick: (value: string) => void;
}

function VirtualKeypadKey({ char, onKeyClick }: Props) {
  const content = useMemo(() => {
    switch (char) {
      case SPECIAL_KEY.backspace: {
        return <Backspace />;
      }
      case SPECIAL_KEY.blank: {
        return null;
      }
      default: {
        return char;
      }
    }
  }, [char]);

  return (
    <td className="keypad__item" onClick={() => onKeyClick(char)}>
      {content}
    </td>
  );
}

export default React.memo(VirtualKeypadKey);
