import * as React from "react";
import { ComponentProps } from "react";

import Txt from "../Text";
import BaseListHeader from "./BaseListHeader";

interface Props extends ComponentProps<typeof BaseListHeader> {
  type?: "A" | "B";
  color?: ComponentProps<typeof Txt>["color"];
}

function ListHeaderRow1({ children, type = "A", color, ...rest }: Props) {
  return (
    <BaseListHeader {...rest}>
      <Txt
        typography={type === "A" ? "t4" : "t5"}
        fontWeight="bold"
        ellipsisAfterLines={1}
        color={color}
      >
        {children}
      </Txt>
    </BaseListHeader>
  );
}

export default ListHeaderRow1;
