import * as React from "react";
import { ComponentProps } from "react";
import { adaptive } from "../../../../../@tossteam/colors";

import Txt from "../Text";
import BaseListHeader from "./BaseListHeader";

interface Props
  extends Omit<ComponentProps<typeof BaseListHeader>, "children"> {
  type?: "A" | "B";
  topText: string;
  topTextColor?: string;
  bottomText: string;
  bottomTextColor?: string;
}

function ListHeaderRow2({
  type = "A",
  topText,
  bottomText,
  topTextColor = adaptive.grey700,
  bottomTextColor = adaptive.grey900,
  ...rest
}: Props) {
  return (
    <BaseListHeader {...rest}>
      <Txt typography={type === "A" ? "t7" : "t5"} color={topTextColor}>
        {topText}
      </Txt>
      <Txt
        typography={type === "A" ? "t3" : "t2"}
        fontWeight="bold"
        color={bottomTextColor}
      >
        {bottomText}
      </Txt>
    </BaseListHeader>
  );
}

export default ListHeaderRow2;
