import classnames from "classnames";
import * as React from "react";
import { LiHTMLAttributes } from "react";
import { adaptive } from "../../../../../@tossteam/colors";
import { Icon } from "../Icon";
import Txt from "../Text";

export interface Props extends LiHTMLAttributes<HTMLLIElement> {}

function ListHeaderSelector({ children, className, ...rest }: Props) {
  const color = adaptive.grey700;
  return (
    <li className={classnames("m-list-header-selector", className)} {...rest}>
      <Txt typography="st10" color={color}>
        {children}
      </Txt>
      <Icon
        className="m-list-header-selector__arrow"
        name="icn-arrow-downwards"
        color={color}
      />
    </li>
  );
}

export default ListHeaderSelector;
