import * as React from "react";
import { ComponentProps } from "react";
import { adaptive, colors } from "../../../../../@tossteam/colors";

import { Text as Txt } from "../Text/Text";
import Icon from "../Icon/Icon";

interface Props extends Pick<ComponentProps<typeof Txt>, "children" | "color"> {
  size: "small" | "medium";
  withArrow?: boolean;
}

function RightText({
  color = colors.grey600,
  size,
  withArrow = false,
  children,
}: Props) {
  return (
    <>
      <Txt typography={size === "small" ? "t7" : "t6"} color={color}>
        {children}
      </Txt>
      {withArrow ? (
        <Icon name="arrow-rightwards-line" size={22} color={color} />
      ) : null}
    </>
  );
}

export default RightText;
