import classnames from "classnames";
import * as React from "react";
import { adaptive } from "../../../../../@tossteam/colors";
import { Icon } from "../Icon";

type ButtonProps = JSX.IntrinsicElements["button"];

interface Props extends ButtonProps {
  downArrow: boolean;
  withArrow: boolean;
}

function NewAgreementRowActionArea({
  children,
  className,
  downArrow,
  withArrow,
  ...rest
}: Props) {
  if (!withArrow && !children) {
    return null;
  }

  return (
    <button
      className={classnames(
        "m-new-agreement-row__action-area",
        "w-reset-button",
        className
      )}
      {...rest}
    >
      {children}
      {withArrow && (
        <span className="m-new-agreement-row__arrow">
          <Icon
            className={classnames("m-new-agreement-row__arrow-icon", {
              "m-new-agreement-row__arrow-icon--down-arrow": downArrow,
            })}
            color={adaptive.grey400}
            name="arrow-rightwards"
          />
        </span>
      )}
    </button>
  );
}

export default NewAgreementRowActionArea;
