import classnames from "classnames";
import * as React from "react";
import { colors } from "../../../../colors";
import NewAgreementRow, { NewAgreementRowProps } from "./NewAgreementRow";

type Props = Omit<
  NewAgreementRowProps,
  "collapsed" | "defaultCollapsed" | "withArrow" | "withBorder"
> & {
  underline?: boolean;
};

function NewAgreementRowSmall({ className, underline = true, ...rest }: Props) {
  return (
    <NewAgreementRow
      className={classnames(
        "m-new-agreement-row-small",
        { "m-new-agreement-row-small--underline": underline },
        className
      )}
      typography="st11"
      fontWeight="regular"
      color={colors.grey500}
      {...rest}
    />
  );
}

export default NewAgreementRowSmall;
