import * as React from "react";
import { ReactNode } from "react";
import { MagnifierProvider } from "src/react/providers/MagnifierContext";
import { ToastProvider } from "src/react/providers/ToastContext";
import { BottomSheetProvider } from "../../providers/BottomSheetContext";
import { DialogProvider } from "../../providers/DialogContext";
import { PortalProvider } from "../../providers/PortalContext";
import useSafeAreaPatch from "../../utils/hooks/useSafeAreaPatch";

interface Props {
  children: ReactNode;
  theme?: "light" | "dark";
}

function TDSProvider({ children }: Props) {
  useSafeAreaPatch();

  return (
    <MagnifierProvider>
      <PortalProvider>
        <ToastProvider>
          <DialogProvider>
            <BottomSheetProvider>{children}</BottomSheetProvider>
          </DialogProvider>
        </ToastProvider>
      </PortalProvider>
    </MagnifierProvider>
  );
}

export default TDSProvider;
