import { storiesOf } from '@storybook/react';
import * as React from 'react';

import { TextButton } from 'src/react';

type TextButtonProps = React.ComponentProps<typeof TextButton>;

storiesOf('TextButton', module)
  .addParameters({})
  .add('type', () => {
    const types: Array<TextButtonProps['type']> = ['primary', 'grey'];

    return (
      <div
        style={{
          padding: '12px',
        }}
      >
        {types.map((type, index) => (
          <TextButton type={type} key={`text-${index}`} withArrow={true}>
            텍스트 버튼
          </TextButton>
        ))}
      </div>
    );
  });
