import { boolean } from '@storybook/addon-knobs';
import { storiesOf } from '@storybook/react';
import * as React from 'react';

import { TextButton } from 'src/react';

type TextButtonProps = React.ComponentProps<typeof TextButton>;

storiesOf('TextButton', module).add('Overview', () => {
  const types: Array<TextButtonProps['type']> = ['primary', 'grey', 'underline'];

  const withArrow = boolean('withArrow', false);
  const disabled = boolean('disabled', false);

  return (
    <>
      {types.map((type, index) => (
        <div
          style={{ border: '1px solid rgba(0, 0, 0, 0.08)', padding: '8px' }}
          key={`text-${index}`}
        >
          <div
            style={{
              padding: '2px',
            }}
          >
            <TextButton type={type} disabled={disabled} withArrow={withArrow}>
              텍스트 버튼 {type}
            </TextButton>
            <br />
          </div>
        </div>
      ))}
      <div style={{ padding: '10px' }}>
        <TextButton type="primary" disabled={disabled} withArrow={withArrow}>
          긴 텍스트 버튼 긴 텍스트 버튼
        </TextButton>
      </div>
    </>
  );
});
