import { action } from '@storybook/addon-actions';
import { storiesOf } from '@storybook/react';
import * as React from 'react';

import { TextFieldBig } from 'src/react';

import Readme from '../README.md';

storiesOf('TextFieldBig', module)
  .addParameters({ readme: { sidebar: Readme } })
  .add('value & onChange', () => {
    function TextFieldBigControlledExample() {
      const [value, setValue] = React.useState('');

      function onChangeTextFieldBig(event: React.ChangeEvent<HTMLInputElement>) {
        setValue(event.target.value);
        action('onChange - TextFieldBig')(...arguments);
      }

      return <TextFieldBig value={value} onChange={onChangeTextFieldBig} />;
    }

    return <TextFieldBigControlledExample />;
  });
