import { format } from 'date-fns';

export default function getCalendarDayAriaLabel(
  day: Date,
  ariaLabelFormat: string,
  {
    isSelected,
    isInSelectedRange,
    isSelectedStart,
    isSelectedEnd,
  }: {
    isSelected?: boolean;
    isInSelectedRange?: boolean;
    isSelectedStart?: boolean;
    isSelectedEnd?: boolean;
  }
) {
  const formattedDate = format(day, ariaLabelFormat);

  if (isSelected) {
    return `${formattedDate}이 선택됨`;
  }

  if (isSelectedStart || isSelectedEnd) {
    if (isSelectedStart && isSelectedEnd) {
      return `${formattedDate}이 선택됨`;
    } else if (isSelectedStart) {
      return `${formattedDate}이 시작날짜로 선택됨`;
    } else if (isSelectedEnd) {
      return `${formattedDate}이 끝날짜로 선택됨`;
    }
  }

  if (isInSelectedRange) {
    return `${formattedDate}이 선택범위에 포함됨`;
  }

  return formattedDate;
}
