import colors from './colors';
import adaptive from './adaptive';
import tds from './tds';
import adaptiveDictionary from './adaptiveDictionary';
import * as utils from './utils';

export function getLightColor(color: keyof typeof adaptiveDictionary) {
  const [lightColor] = adaptiveDictionary[color];
  const colorValue = colors[lightColor as keyof typeof colors];

  return colorValue ?? lightColor;
}

export function getDarkColor(color: keyof typeof adaptiveDictionary) {
  const [, darkColor] = adaptiveDictionary[color];
  const colorValue = colors[darkColor as keyof typeof colors];

  return colorValue ?? darkColor;
}

export const lightCSS = utils.generateLightCSS();
export const darkCSS = utils.generateDarkCSS();
export const colorsCSS = utils.generateColorsCSS();
export const lightTDSVariablesCSS = utils.generateLightTDSVariablesCSS();
export const darkTDSVariablesCSS = utils.generateDarkTDSVariablesCSS();

export { adaptive, colors, tds };
