import classnames from "classnames";
import * as React from "react";
import { HTMLProps } from "react";
import convertNewLineToJSX from "../../utils/convertNewLineToJSX";
import TextButton from "../TextButton";

interface Props extends Omit<HTMLProps<HTMLLIElement>, "label" | "onClick"> {
  onClick?(event: React.MouseEvent<HTMLButtonElement>): void;
}

export default function AgreementRow03({
  className,
  children,
  onClick,
  ...props
}: Props) {
  return (
    <li className={classnames("agreement-row-03", className)} {...props}>
      <TextButton
        type="grey"
        withArrow={true}
        onClick={onClick}
        className={classnames(
          "agreement-row-03__text-button",
          "typography-st11"
        )}
      >
        {typeof children === "string"
          ? convertNewLineToJSX(children)
          : children}
      </TextButton>
    </li>
  );
}
