import classnames from "classnames";
import * as React from "react";
import { HTMLProps, ReactNode } from "react";
import Checkbox from "../Checkbox";
import AgreementRightArrow from "./AgreementRightArrow";

interface Props
  extends Omit<HTMLProps<HTMLLIElement>, "label" | "checked" | "onChange"> {
  checked?: boolean;
  onChange?(event: React.ChangeEvent<HTMLInputElement>, checked: boolean): void;
  onLabelClick?(event: React.MouseEvent<HTMLButtonElement>): void;
  right?: ReactNode;
  rightFixed?: boolean;
}

export default function AgreementRow04({
  checked,
  onChange,
  className,
  onLabelClick,
  children,
  right = (
    <AgreementRightArrow
      className="agreement-row-04__right"
      onClick={onLabelClick}
    />
  ),
  rightFixed = false,
  ...props
}: Props) {
  return (
    <li
      className={classnames(
        "agreement-row-04",
        { "agreement-row-04--right-fixed": rightFixed },
        className
      )}
      {...props}
    >
      <Checkbox
        className="agreement-row-04__check typography-t6"
        type="primary"
        checked={checked}
        onChange={onChange}
      >
        <span>{children}</span>
      </Checkbox>
      {right}
    </li>
  );
}
