import { action } from "@storybook/addon-actions";
import { boolean } from "@storybook/addon-knobs";
import { storiesOf } from "@storybook/react";
import * as React from "react";
import Agreement from "../Agreement";
import Readme from "../README.md";

storiesOf("Agreement", module)
  .addParameters({
    sidebar: Readme,
  })
  .addDecorator((storyFn) => <Agreement.List>{storyFn()}</Agreement.List>)
  // `AgreementRow04`은 단독으로 사용하지 않고 `AgreementList` 안에 넣어 사용합니다.
  .add("Agreement.Row04", () => {
    function AgreementRow04Example() {
      const [checked, setChecked] = React.useState(false);

      function onChange(event: React.ChangeEvent<HTMLInputElement>) {
        setChecked(event.target.checked);
        action("onChange - Agreement.Row04")(...arguments);
      }

      return (
        <>
          <Agreement.Row04
            checked={checked}
            onChange={onChange}
            onLabelClick={action("onLabelClick - Agreement.Row04")}
          >
            통합 금융정보 서비스 약관 1
          </Agreement.Row04>
          <Agreement.Row04
            checked={checked}
            onChange={onChange}
            onLabelClick={action("onLabelClick - Agreement.Row04")}
          >
            통합 금융정보 서비스 약관 2
          </Agreement.Row04>
        </>
      );
    }

    return <AgreementRow04Example />;
  })
  .add("Agreement.Row04 Right", () => {
    function AgreementRow04Example() {
      const [checked, setChecked] = React.useState(false);

      function onChange(event: React.ChangeEvent<HTMLInputElement>) {
        setChecked(event.target.checked);
        action("onChange - Agreement.Row04")(...arguments);
      }

      return (
        <>
          <Agreement.Row04
            checked={checked}
            onChange={onChange}
            onLabelClick={action("onLabelClick - Agreement.Row04")}
            rightFixed={boolean("rightFixed", true)}
          >
            통합 금융정보 서비스 약관 1
          </Agreement.Row04>
        </>
      );
    }

    return <AgreementRow04Example />;
  });
