import * as React from "react";
import { memo } from "react";

interface Props {
  id: string;
  width: number;
  height: number;
  radius: number;
}

function BezierMask({ id, width, height, radius }: Props) {
  return (
    <svg className="visually-hidden" width="0" height="0">
      <clipPath id={id}>
        <path
          d={`M${radius} 0 H ${
            width - radius
          } Q ${width} 0 ${width} ${radius} V ${
            height - radius
          } Q ${width} ${height} ${
            width - radius
          } ${height} H ${radius} Q 0 ${height} 0 ${
            height - radius
          } V ${radius} Q 0 0 ${radius} 0`}
        />
      </clipPath>
    </svg>
  );
}

export default memo(BezierMask);
