import * as React from "react";
import { memo } from "react";

interface Props {
  id: string;
  width: number;
  height: number;
  radius: number;
}

function BezierMaskTop({ id, width, height, radius }: Props) {
  return (
    <svg width="0" height="0">
      <clipPath id={id}>
        <path
          d={`M${radius} 0 H ${
            width - radius
          } Q ${width} 0 ${width} ${radius} V ${height} H 0 V ${radius} Q 0 0 ${radius} 0`}
        />
      </clipPath>
    </svg>
  );
}

export default memo(BezierMaskTop);
