import classnames from 'classnames';
import * as React from 'react';

export interface Props {
  className?: string;
}

export default class BottomInfoItem extends React.PureComponent<Props> {
  public render() {
    return (
      <li className={classnames(`bottom-info__item`, 'font-size--13', this.props.className)}>
        {this.props.children}
      </li>
    );
  }
}
