import { storiesOf } from '@storybook/react';
import * as React from 'react';
import { action } from '@storybook/addon-actions';
import { select, boolean } from '@storybook/addon-knobs';

import Button from '../Button';

storiesOf('Button', module)
  .add('Overview', () => {
    const type = select('Type', ['primary', 'danger', 'light', 'dark'], 'primary');
    const size = select('Size', ['big', 'large', 'medium', 'tiny'], 'big');
    const display = select('Display', ['inline', 'block', 'full'], 'inline');
    const as = select('As', ['a', 'button', 'div'], 'button');
    const loading = boolean('loading', false);
    const disabled = boolean('disabled', false);
    const buttonProps = {
      onClick: action('버튼 눌림'),
      type,
      as,
      size,
      display,
      loading,
      disabled,
    } as any;

    return (
      <div
        style={{
          padding: '24px',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'flex-start',
        }}
      >
        <Button {...buttonProps} htmlStyle={{ marginBottom: '12px' }}>
          기본 버튼
        </Button>
        <Button style="weak" {...buttonProps}>
          Weak 버튼
        </Button>
      </div>
    );
  })
  .add('Type Check', () => {
    return (
      <>
        <Button htmlType="submit" htmlStyle={{ margin: '24px' }}>
          제출하기
        </Button>
        <Button as="a" href="https://toss.im" target="_blank">
          홈페이지 가기
        </Button>
        <Button as="div">DIV 버튼</Button>
      </>
    );
  });
