import CalendarModifier from './CalendarModifier';

interface Params {
  date: Date;
  thisMonth?: Date;
  isOutsideDay?: boolean;
}

export type CalendarModifierFn = (params: Params) => boolean;

type CalendarModifierSet = Partial<Record<CalendarModifier, CalendarModifierFn>>;

export type CalendarModifierSetOf<T extends Partial<CalendarModifier>> = Record<
  T,
  CalendarModifierFn
>;

export default CalendarModifierSet;
