import { storiesOf } from '@storybook/react';
import * as React from 'react';

import Carousel from 'src/react/components/Carousel';

import Readme from '../README.md';
import SimpleSlide from './SimpleSlide';

storiesOf('Carousel', module)
  .addParameters({ readme: { sidebar: Readme } })
  .add('autoplay', () => {
    const extraProps = { spaceBetween: 16, slidesPerView: 1.2 };

    return (
      <>
        {[
          false,
          true,
          {
            delay: 1000,
            stopOnLastSlide: false,
            disableOnInteraction: false,
            reverseDirection: true,
            waitForTransition: false,
          },
        ].map(autoplay => (
          <>
            <Carousel {...extraProps} autoplay={autoplay}>
              {[1, 2, 3, 4, 5].map(index => (
                <Carousel.Slide key={index}>
                  <SimpleSlide>autoplay = {JSON.stringify(autoplay, null, 2)}</SimpleSlide>
                </Carousel.Slide>
              ))}
            </Carousel>
            <br />
          </>
        ))}
      </>
    );
  });
