import { storiesOf } from '@storybook/react';
import * as React from 'react';

import Carousel from 'src/react/Carousel';

import Readme from '../README.md';
import SimpleSlide from './SimpleSlide';

storiesOf('Carousel', module)
  .addParameters({ readme: { sidebar: Readme } })
  .add('speed', () => {
    const autoplay = {
      delay: 1000,
      stopOnLastSlide: false,
      disableOnInteraction: false,
      reverseDirection: true,
      waitForTransition: true,
    };

    const extraProps = { spaceBetween: 16, slidesPerView: 1.2 };

    return (
      <>
        <p>
          <code>AutoplayOption.waitForTransition</code>가<code>true</code>
          이기를 권장합니다. (디폴트값은 👉 true)
        </p>
        {[100, 200, 400, 800, 1600].map(speed => (
          <React.Fragment key={speed}>
            <Carousel {...extraProps} autoplay={autoplay} speed={speed}>
              {[1, 2, 3, 4, 5].map(index => (
                <Carousel.Slide key={index}>
                  <SimpleSlide>Duration of transition between slides (in ms): {speed}</SimpleSlide>
                </Carousel.Slide>
              ))}
            </Carousel>
            <br />
          </React.Fragment>
        ))}
      </>
    );
  });
