import cx from "classnames";
import * as React from "react";
import { ComponentProps, ReactNode } from "react";
import Modal from "../Modal";

interface Props extends Omit<ComponentProps<typeof Modal>, "fixed"> {
  children: ReactNode;
}

function DialogWithCTA({
  className,
  children,
  closeOnDimmerClick = false,
  ...rest
}: Props) {
  return (
    <Modal
      className={cx(
        "modal-dialog__card",
        "modal-dialog__card--with-cta",
        className
      )}
      closeOnDimmerClick={closeOnDimmerClick}
      {...rest}
    >
      <div className="modal-dialog__body">{children}</div>
    </Modal>
  );
}

export default DialogWithCTA;
