import classnames from "classnames";
import * as React from "react";
import { ImgHTMLAttributes } from "react";

export interface Props extends ImgHTMLAttributes<HTMLImageElement> {
  className?: string;
  type?:
    | "default"
    | "square"
    | "rectangle"
    | "rectangle-small"
    | "circle"
    | "circle-small";
}

export default function ListRowImage({
  className,
  type = "default",
  ...imgAttributes
}: Props) {
  return (
    <div
      className={classnames(
        "list-row__image",
        {
          [`list-row__image--type-${type}`]: type,
        },
        className
      )}
    >
      <img {...imgAttributes} className="list-row__image__inner" />
    </div>
  );
}
